package kingCounty;

import org.openqa.selenium.By;

import org.openqa.selenium.WebDriver;
import core.CommonLib;

public class CaseLoadCheck extends CommonLib{

	By TickIcon= By.xpath(CommonLib.readElementPropertyFile("ICON_SUBJECT_CHECKBUTTON_XP"));
	By SubjectQueue =By.xpath(CommonLib.readElementPropertyFile("FIELD_SUBJECTQUEUE_XP"));
	
	//********************************************************************************
	// Function	Name	  : ClickonTichIconSubject                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
	// Summary	          : Function to check details are displayed					
	//********************************************************************************
		
	public void ClickonTichIconSubject() throws Exception {
		driver.findElement(TickIcon).click();
		Thread.sleep(5000);
		CommonLib.highLighterMethod(driver, driver.findElement(SubjectQueue));
	}
	
	
}
 