package kingCounty;

import org.openqa.selenium.By;
import org.openqa.selenium.WebDriver;

import core.CommonLib;

public class DesignDisplay extends CommonLib { 
	
	//WebDriver driver;
	By SubjectName= By.xpath(CommonLib.readElementPropertyFile("LABEL_SUBJECTGENDER_XP"));
	By SubjectDate= By.xpath(CommonLib.readElementPropertyFile("LABEL_SUBJECTDATE_XP"));
	By SubjectCCN= By.xpath(CommonLib.readElementPropertyFile("LABEL_SUBJECTCCN_XP"));
	By MenuCaseload= By.xpath(CommonLib.readElementPropertyFile("LINK_MENUMYCASELOAD_XP"));
	By MenuCloseIcon= By.xpath(CommonLib.readElementPropertyFile("ICON_MENUCLOSE_XP"));
	By SubjectTile= By.xpath(CommonLib.readElementPropertyFile("HOME_SUBJECTCASELOAD_XP"));
	
	//********************************************************************************
	// Function	Name	  : AssertNullSubjectDetails                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
	// Summary	          : Function to check details are displayed					
	//********************************************************************************
		
	public void AssertNullSubjectDetails() throws Exception {
	
		SubjectProfile objSubj= new SubjectProfile();
		objSubj.subjectProfileClick();
		CommonLib.assertTrueFn(SubjectName);
 		CommonLib.assertTrueFn(SubjectCCN); 
	}
	
	//********************************************************************************
	// Function	Name	  : ClickonMenuCaseLoad                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
	// Summary	          : Function to click on My case load under Menu					
	//********************************************************************************
		
	public void ClickonMenuCaseLoad() throws Exception {
		
		MenuOption objMenuOption= new MenuOption();
		objMenuOption.ClickonMenu();
		Thread.sleep(4000);
		CommonLib.executescriptclick(driver.findElement(MenuCaseload));
	}
	  
	//********************************************************************************
	// Function	Name	  : ClickonMenuCloseIcon                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
	// Summary	          : Function to click on close button under Menu					
	//********************************************************************************
		
	public void ClickonMenuCloseIcon() {
		driver.findElement(MenuCloseIcon).click();
	}
	
	//********************************************************************************
	// Function	Name	  : HighlightSubjectTile                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
	// Summary	          : Function to Highlight subject Tile					
	//********************************************************************************
		
	public void HighlightSubjectTile() throws Exception {
		
		CommonLib.highLighterMethod(driver, driver.findElement(SubjectTile));
	}

}
